    private void btnTestTrueLoveMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnTestTrueLoveMouseClicked
        // Start a counter for TRUELOVE
        int trueLoveCounter = 0;
        
        // Get Values from Text Boxes
        String yourName = txtYourName.getText();
        String theirName = txtTheirName.getText();
        
        // Combine names and clean spaces for efficiency
        String names = yourName + theirName;
        names = names.replace(" ", "");
        
        // Call the name length once instead of on each loop
        int nameLength = names.length();
        for ( int i = 0; i < nameLength; i++ )
        {
            // Get one letter of the names
            String currentLetter = names.substring(i, i+1);
            
            // Check if it has TRULOV and add 1 if it does
            // If it is an E, add 2
            if  ( 
                    currentLetter.equalsIgnoreCase("T") ||
                    currentLetter.equalsIgnoreCase("R") ||
                    currentLetter.equalsIgnoreCase("U") ||
                    currentLetter.equalsIgnoreCase("L") ||
                    currentLetter.equalsIgnoreCase("O") ||
                    currentLetter.equalsIgnoreCase("V")
                )
                trueLoveCounter += 1;
            else if ( currentLetter.equalsIgnoreCase("E") )
                trueLoveCounter += 2;
        }
        
        // Check Results
        if (trueLoveCounter >= 10)
            lblResult.setText("You Found a Match!");
        else
            lblResult.setText("Go join a convent!");
    }//GEN-LAST:event_btnTestTrueLoveMouseClicked

